mapper.select:
SELECT *
FROM device
WHERE 1=1

    AND id = #{id}


    AND projectID = #{projectID}


    AND deviceowner = #{deviceowner}


    AND deadline = #{deadline}


    AND status = #{status}

LIMIT #{pageFirst},#{pageLast}

SELECT count(*)
FROM device
WHERE 1=1

    AND id = #{id}


    AND projectID = #{projectID}


    AND deviceowner = #{deviceowner}


    AND deadline = #{deadline}


    AND status = #{status}

mapper.update:
update device
SET projectID = #{projectID},
deviceowner = #{deviceowner},
deadline = #{deadline},
status = #{status}
where id = #{id}

mapper.delete:
DELETE FROM device
WHERE id IN

    #{id}
