mapper.update:
update workhourinfo
set status = 1
where id = #{workHourId}

update workhourinfo
set status = 2
where id = #{workHourId}

update workhourinfo
set finished_function =#{finishedFunction},
    finished_activity =#{finishedActivity},
    start_time =#{startTime},
    finish_time =#{finishTime}
where id = #{workHourId}
